import React from 'react';
//import ReactDOM from 'react-dom';
import Slider from 'rc-slider';
import CountUp from 'react-countup';
import { Line } from 'rc-progress';

export class App4 extends React.Component
{
	constructor(props)
	{
		super(props);
		this.state = {
			now: 0,
			end: 100,
			valueSlider: 0,
			buttonCSSStyle:{width:200, height:30, fontSize:20},
			labelCSSStyle:{marginLeft: 10, marginRight: 100, fontSize:20},
			sliderCSSStyle:{marginLeft: 10, marginRight: 100, fontSize:20},
		}
	}		

	sliderChange = (value) =>
	{
		this.setState({valueSlider: value});
		if(value > this.state.now)
		{
			this.setState({now: 0});
		}
	}

	reset = () =>
	{
		this.setState({now: 0});
	}

	render() {
		const { buttonCSSStyle, labelCSSStyle, sliderCSSStyle, now, end, valueSlider } = this.state;
		return (
		    <div>
				<Line percent={now}></Line>
				<CountUp style={labelCSSStyle} start="0" now={now} end={end} duration="100" delay="0"></CountUp>
				<Slider
					min={0}
					max={end}
					value={valueSlider}
					style={sliderCSSStyle} 
					onChange={this.sliderChange.bind(this)}>
				</Slider>
				<button style={buttonCSSStyle} onClick={this.reset.bind(this)}>Reset</button>
			</div>
		);
	}
}

export default App4;
