import React from 'react';
//import ReactDOM from 'react-dom';
import CountUp from 'react-countup';
import Slider from 'rc-slider';

export class App4 extends React.Component
{
	constructor(props)
	{
		super(props);
		
		this.state = {
			now: 0,
			end: 100,
			valueSlider: 0,
			buttonCSSStyle:{width:200, height:30, fontSize:20},
			labelCSSStyle:{marginLeft: 10, marginRight: 100, fontSize:20},
			sliderCSSStyle:{marginLeft: 10, marginRight: 100, fontSize:20},
		}
	}		

	sliderChange = (value) =>
	{
		alert("click "+value);
		this.setState({valueSlider: value});
		if(value > this.state.now)
		{
			this.setState({now: 0});
		}
	}

	reset = () =>
	{
		this.setState({now: 0});
	}

	render() {
		const { buttonCSSStyle, labelCSSStyle, sliderCSSStyle, now, end, valueSlider } = this.state;
		const sliderProps = {
  			min: 0.0,
  			max: 1.0,
  			step: 0.05,
  			marks: { 0.0: 0, 0.1: 10, 0.2: 20, 0.3: 30, 0.4: 40, 0.5: 50, 0.6: 60, 0.7: 70, 0.8: 80, 0.9: 90, 1.0: 100 }
		}
		return (
		    <div>
				<CountUp style={labelCSSStyle} start="0" now={now} end={end} duration="10" delay="0"></CountUp>
				<Slider color="red" inverted={false}
                	value={now}
      				onChange={val => this.state.now}
      					{...sliderProps}
				/>
				<button style={buttonCSSStyle} onClick={this.reset.bind(this)}>Reset</button>
			</div>
		);
	}
}

export default App4;
