import React from 'react';
//import ReactDOM from 'react-dom';
import CountUp from 'react-countup';
import Slider from 'react-input-slider';

export class App4 extends React.Component
{
	constructor(props)
	{
		super(props);
		
		this.state = {
			start: 0,
			end: 100,
			x: 0,
			xmin: 0,
			xmax: 1000,
			reset: false,
			buttonCSSStyle:{width:200, height:30, fontSize:20},
			labelCSSStyle:{marginLeft: 10, marginRight: 100, fontSize:20},
			sliderCSSStyle:{marginLeft: 10, marginRight: 100, fontSize:20},
		}
	}		

	sliderChange = (x) =>
	{
		alert(x.x);
		//this.setState({x: x.x});
		if(x.x > this.state.now)
	    {
			this.setState({x: 0});
		}
	}

	reset = () =>
	{
		this.setState({x: 0});
		this.setState({reset: true});
	}

	render() {
		const { buttonCSSStyle, labelCSSStyle, sliderCSSStyle, start, end, x, xmin, xmax} = this.state;
		
		return (
		    <div>
				<CountUp style={labelCSSStyle} start={start} now={x} end={end} duration="10" delay="0"></CountUp>
				<Slider style={sliderCSSStyle} inverted={false}
                	x={x} xmin={xmin} xmax={xmax} axis="x"
					onChange={this.sliderChange.bind(this)}>
			    </Slider>	
				<button style={buttonCSSStyle} onClick={this.reset.bind(this)}>Reset</button>
			</div>
		);
	}
}

export default App4;
